module.exports.future = function() {
    var fulfillResult;
    console.log('Hello from future');

    this.fulfill = function(err, result) {
        console.log('fulfilling');
        fulfillResult = result;
    }

    // future.when is called by dynamic_module_loader_test. Mocking it to pass the test.
    this.when = function(callback) {
        callback(undefined, fulfillResult);
    }
};
